mapper.select:

SELECT 	'0000' AS RESULTCODE
		, #{year} AS YEAR
		, #{month} AS MONTH
		, #{date} AS DATE
        , COUNT(DISTINCT USERID) AS TOTALCNT
FROM REQUESTINFO
WHERE DATE_FORMAT(CREATEDATE, '%Y') = #{year}
  AND DATE_FORMAT(CREATEDATE, '%m') = #{month}
  AND DATE_FORMAT(CREATEDATE, '%d') = #{date}
